DROP TABLE IF EXISTS mp_estrella_db.dbo.dim_zona;

CREATE TABLE mp_estrella_db.dbo.dim_zona (
    id_zona            int not null,
	comuna             varchar(100) not null,
	provincia          varchar(100) not null,
	region             varchar(100) not null,
	numero_region      int,
	CONSTRAINT PK_zona PRIMARY KEY (id_zona)
);



UPDATE mp_estrella_db.dbo.dim_zona
   SET numero_region = FLOOR(id_zona / 1000);